var debug = require('debug')('box-api:cache');

function Cache() {
  this._cache = {};
}

Cache.prototype.get = function(type, id) {
  return this._cache[type] && this._cache[type][id];
};

Cache.prototype.store = function(obj) {
  debug('storing %s in cache', obj);
  var typeCache = this._cache[obj.data.type] = this._cache[obj.data.type] || {};
  typeCache[obj.data.id] = obj;
};

module.exports = Cache;
