function File(client, data) {
  this.client = client;
  this.data = data;
}

File.prototype.toString = function() {
  return '<File id="' + this.data.id + '" name="' + this.data.name + '">';
};

File.prototype.update = function(file, options, callback) {
  this.client.file.update(this.data.id, file, options, callback);
};

module.exports = File;
